css:
.border {
    border: 1px solid #dee2e6;
}
.border-left {
    border-left: 1px solid #dee2e6;
    margin-left: -1px;
}
.border-right {
    border-right: 1px solid #dee2e6;
}
.align-middle {
    vertical-align: middle;
}
.h-30 {
        height: 30px;
    }
.mh-30 {
    min-height: 10px;
}
.h-100 {
    height: 100%;
}
.mh-100 {
    max-height: 100%;
}
.d-inline-block {
    display: inline-block;
}
.row-no-margin {
  margin-top:-1px;
  margin-right: -1px;
  margin-left: -1px;
  /*height: 25px;*/
}
.parent {
  display: flex;
  align-items: stretch;
}
.child {
  flex: 1;
}
.bgcolor {
    background-color: beige;
}
.right_align {
  text-align: right;
  /*border: 1px solid green;*/
}
.nopadding {
   padding: 0 !important;
   margin: 0 !important;
}
.separator {
  background-color: beige; /* Gray color */
  height: 10px; /* Adjust the height of the separator as needed */
}

[document-status]{
    margin-bottom: 0;
    visibility: hidden;
    height:0;
}

.mid_center_align {
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
}
.rigth_center_align {
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: right;
}
.left_center_align {
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: left;
}
.padding_left_right {
    padding-left: 5px;
    padding-right: 5px;
}
.margin_left_right {
    margin-left: 5px;
    margin-right: 5px;
}
.page_break_after {
    page-break-after: always;
}
.page_break_before {
    page-break-before: always;
}
.title_1 {
    font-size: 1.2rem; 
}
.title_2 {
    font-size: 1rem; 
}
.title_3 {
    font-size: 0.8rem; 
}

.font_bold {
    font-weight: bold;
}

.bg_green {
    background-color: #E9F6DE;
}
.bg_gray {
    background-color: #F4F5F6;
}
.signature {
    height: 60px;
}

.mid_left_align {
    height: 100%;
    display: flex;
    align-items: center;
    /*justify-content: center;*/
}

