  <div class="col-sm-3 nopadding h-30">
      <small class="mid_left_align margin_left_right">PROJECT LEADER</small>
  </div>
  <div class="col-sm-9 border-left nopadding h-30"  >
      <div class="col-sm-5 nopadding "> <small class="margin_left_right">{{frappe.db.get_value("User", doc.leader,"full_name")}}</small></div>
      <div class="col-sm-4 nopadding " > <small class="margin_left_right">END DATE: {{" "}}{{ doc.end_date }}</small></div>
