css:
.border {
    border: 1px solid #dee2e6;
}
.border-left {
    border-left: 1px solid #dee2e6;
}
.border-right {
    border-right: 1px solid #dee2e6;
}
.align-middle {
    vertical-align: middle;
}
.h-100 {
    height: 100%;
}
.mh-100 {
    max-height: 100%;
}
.d-inline-block {
    display: inline-block;
}
.row-no-margin {
  margin-top: 0;
  margin-right: -15px;
  margin-left: -15px;
}
.parent {
  display: flex;
  align-items: stretch;
}
.child {
  flex: 1;
}
.bgcolor {
    background-color: beige;
}
